$(function() {
	console.log("About us loaded");

	$("a[href='#tab3']").click(function(event){
		console.log("About us clicked");
		fillAboutUsMail();
	});

	function fillAboutUsMail(){
		//vEmail var is generated by a helper in the about_us view.
		//view.aboutus.js is imported in this view, so, its not possible to call fillAboutUsEmail without vEmail var.
		if(typeof vEmail != undefined){
			var mailLink = $("#vishEditorEmail");
			var mailSpan = $(mailLink).find("span");
			$(mailLink).attr("href","mailto:" + vEmail);
			$(mailSpan).html(vEmail);
		}
	}
});